import React, { useState, useEffect, useMemo } from 'react';
import {
  Box,
  TextField,
  Button,
  Typography,
  Paper,
  Stack,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  SelectChangeEvent,
  useTheme,
  Stepper,
  Step,
  StepLabel,
  Grid,
  Fade,
  Avatar,
  Chip,
  CircularProgress,
  FormHelperText
} from '@mui/material';
import {
  EventAvailable as CalendarIcon,
  DirectionsBike as BikeIcon,
  Person as PersonIcon,
  Check as CheckIcon
} from '@mui/icons-material';
import { Booking, BikeService } from '../types';
import { bikeData, BikeModel } from '../data/bikeData';
import { backgroundImageStyles, overlayStyles } from '../styles/common';

interface BookingFormProps {
  services: BikeService[];
  onSubmit: (booking: Omit<Booking, 'id' | 'status'>) => void;
  loading?: boolean;
}

const validateEmail = (email: string): boolean => {
  const re = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return re.test(email);
};

const validatePhone = (phone: string): boolean => {
  // Basic phone validation - can be adjusted based on your requirements
  const re = /^\+?[\d\s-]{10,}$/;
  return re.test(phone);
};

const validateDate = (date: string): boolean => {
  const selectedDate = new Date(date);
  const now = new Date();
  return selectedDate > now;
};

const BookingForm: React.FC<BookingFormProps> = ({ services, onSubmit, loading = false }) => {
  const theme = useTheme();
  const [activeStep, setActiveStep] = useState(0);
  const [formData, setFormData] = useState({
    customerName: '',
    customerEmail: '',
    customerPhone: '',
    bikeBrand: '',
    bikeModel: '',
    manufactureYear: '',
    serviceId: '',
    appointmentDate: '',
    notes: ''
  });

  const [selectedBikeDetails, setSelectedBikeDetails] = useState<BikeModel | null>(null);

  const steps = ['Select Bike', 'Select Service', 'Personal Details', 'Confirm Booking'];

  // Generate years array from 2000 to current year
  const years = useMemo(() => {
    const currentYear = new Date().getFullYear();
    return Array.from({ length: currentYear - 1999 }, (_, i) => (currentYear - i).toString());
  }, []);

  const [errors, setErrors] = useState<Record<string, string>>({});

  const validateField = (name: string, value: string): string => {
    switch (name) {
      case 'customerEmail':
        return !validateEmail(value) ? 'Please enter a valid email address' : '';
      case 'customerPhone':
        return !validatePhone(value) ? 'Please enter a valid phone number' : '';
      case 'appointmentDate':
        return !validateDate(value) ? 'Please select a future date and time' : '';
      default:
        return !value ? 'This field is required' : '';
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleSelectChange = (e: SelectChangeEvent) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error when user makes a selection
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }

    // Reset dependent fields when brand changes
    if (name === 'bikeBrand') {
      setFormData(prev => ({
        ...prev,
        bikeModel: ''
      }));
      setSelectedBikeDetails(null);
    }

    // Update bike details when model is selected
    if (name === 'bikeModel') {
      const brand = bikeData.find(b => b.brand === formData.bikeBrand);
      const model = brand?.models.find(m => m.name === value);
      setSelectedBikeDetails(model || null);
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    // Validate all fields before submission
    const isValid = validateStep();
    if (!isValid) return;

    onSubmit({
      ...formData,
      appointmentDate: new Date(formData.appointmentDate)
    });
  };

  const validateStep = () => {
    const newErrors: Record<string, string> = {};

    switch (activeStep) {
      case 0:
        if (!formData.bikeBrand) newErrors.bikeBrand = 'Please select a bike brand';
        if (!formData.bikeModel) newErrors.bikeModel = 'Please select a bike model';
        if (!formData.manufactureYear) newErrors.manufactureYear = 'Please select manufacture year';
        break;
      case 1:
        if (!formData.serviceId) newErrors.serviceId = 'Please select a service';
        break;
      case 2:
        if (!formData.customerName) newErrors.customerName = 'Please enter your name';
        if (!formData.customerEmail) {
          newErrors.customerEmail = 'Please enter your email';
        } else if (!validateEmail(formData.customerEmail)) {
          newErrors.customerEmail = 'Please enter a valid email address';
        }
        if (!formData.customerPhone) {
          newErrors.customerPhone = 'Please enter your phone number';
        } else if (!validatePhone(formData.customerPhone)) {
          newErrors.customerPhone = 'Please enter a valid phone number';
        }
        break;
      case 3:
        if (!formData.appointmentDate) {
          newErrors.appointmentDate = 'Please select an appointment date';
        } else if (!validateDate(formData.appointmentDate)) {
          newErrors.appointmentDate = 'Please select a future date and time';
        }
        break;
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleNext = () => {
    if (validateStep()) {
      setActiveStep((prevStep) => prevStep + 1);
    }
  };

  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

  const selectedService = services.find(service => service.id === formData.serviceId);
  const availableModels = bikeData.find(b => b.brand === formData.bikeBrand)?.models || [];

  return (
    <Box
      sx={{
        position: 'relative',
        borderRadius: 4,
        overflow: 'hidden',
        minHeight: '600px',
        opacity: loading ? 0.7 : 1,
        pointerEvents: loading ? 'none' : 'auto',
      }}
    >
      {loading && (
        <Box
          sx={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            zIndex: 1,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            gap: 2,
          }}
        >
          <CircularProgress />
          <Typography>Submitting your booking...</Typography>
        </Box>
      )}

      {/* Background Image */}
      <Box
        sx={{
          ...backgroundImageStyles,
          backgroundImage: 'url("https://images.unsplash.com/photo-1565965314171-6a937d870545?ixlib=rb-1.2.1&auto=format&fit=crop&w=1920&q=80")',
          opacity: 0.15
        }}
      />

      <Paper 
        elevation={0}
        sx={{ 
          position: 'relative',
          background: 'transparent',
          backdropFilter: 'blur(10px)',
          p: { xs: 2, md: 4 },
          borderRadius: 4
        }}
      >
        <Typography 
          variant="h3" 
          component="h2" 
          gutterBottom 
          align="center"
          sx={{
            color: theme.palette.primary.main,
            fontWeight: 'bold',
            fontSize: { xs: '2rem', md: '2.5rem' },
            mb: 4
          }}
        >
          Book Your Service
        </Typography>

        <Stepper 
          activeStep={activeStep} 
          sx={{ 
            mb: 5,
            '& .MuiStepLabel-label': {
              color: theme.palette.text.secondary,
              '&.Mui-active': {
                color: theme.palette.primary.main,
                fontWeight: 'bold'
              }
            }
          }}
        >
          {steps.map((label) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
            </Step>
          ))}
        </Stepper>

        <Box component="form" onSubmit={handleSubmit}>
          <Fade in={true} timeout={500}>
            <Stack spacing={3}>
              {activeStep === 0 && (
                <>
                  <FormControl fullWidth required error={!!errors.bikeBrand}>
                    <InputLabel>Select Brand</InputLabel>
                    <Select
                      name="bikeBrand"
                      value={formData.bikeBrand}
                      label="Select Brand"
                      onChange={handleSelectChange}
                      startAdornment={
                        <BikeIcon sx={{ mr: 1, color: theme.palette.primary.main }} />
                      }
                    >
                      {bikeData.map((brand) => (
                        <MenuItem key={brand.brand} value={brand.brand}>
                          {brand.brand}
                        </MenuItem>
                      ))}
                    </Select>
                    {errors.bikeBrand && (
                      <FormHelperText>{errors.bikeBrand}</FormHelperText>
                    )}
                  </FormControl>

                  {formData.bikeBrand && (
                    <FormControl fullWidth required>
                      <InputLabel>Select Model</InputLabel>
                      <Select
                        name="bikeModel"
                        value={formData.bikeModel}
                        label="Select Model"
                        onChange={handleSelectChange}
                      >
                        {availableModels.map((model) => (
                          <MenuItem key={model.name} value={model.name}>
                            {model.name} - {model.engine}
                          </MenuItem>
                        ))}
                      </Select>
                    </FormControl>
                  )}

                  <FormControl fullWidth required>
                    <InputLabel>Year of Manufacture</InputLabel>
                    <Select
                      name="manufactureYear"
                      value={formData.manufactureYear}
                      label="Year of Manufacture"
                      onChange={handleSelectChange}
                    >
                      {years.map((year) => (
                        <MenuItem key={year} value={year}>
                          {year}
                        </MenuItem>
                      ))}
                    </Select>
                  </FormControl>

                  {selectedBikeDetails && (
                    <Paper 
                      variant="outlined" 
                      sx={{ 
                        p: 2, 
                        bgcolor: 'rgba(255, 255, 255, 0.9)',
                        borderColor: theme.palette.primary.main
                      }}
                    >
                      <Typography variant="subtitle1" color="primary" gutterBottom>
                        Bike Details
                      </Typography>
                      <Grid container spacing={2}>
                        <Grid item xs={12} sm={6}>
                          <Typography variant="body2" color="text.secondary">
                            Type: {selectedBikeDetails.type}
                          </Typography>
                        </Grid>
                        <Grid item xs={12} sm={6}>
                          <Typography variant="body2" color="text.secondary">
                            Engine: {selectedBikeDetails.engine}
                          </Typography>
                        </Grid>
                        <Grid item xs={12}>
                          <Typography variant="body2" color="text.secondary" gutterBottom>
                            Features:
                          </Typography>
                          <Stack direction="row" spacing={1} flexWrap="wrap" gap={1}>
                            {selectedBikeDetails.features.map((feature, index) => (
                              <Chip
                                key={index}
                                label={feature}
                                size="small"
                                color="primary"
                                variant="outlined"
                              />
                            ))}
                          </Stack>
                        </Grid>
                      </Grid>
                    </Paper>
                  )}
                </>
              )}

              {activeStep === 1 && (
                <>
                  <FormControl fullWidth required error={!!errors.serviceId}>
                    <InputLabel>Select Service</InputLabel>
                    <Select
                      name="serviceId"
                      value={formData.serviceId}
                      label="Select Service"
                      onChange={handleSelectChange}
                      startAdornment={
                        <BikeIcon sx={{ mr: 1, color: theme.palette.primary.main }} />
                      }
                    >
                      {services.map((service) => (
                        <MenuItem key={service.id} value={service.id}>
                          {service.name} - ${service.price}
                        </MenuItem>
                      ))}
                    </Select>
                    {errors.serviceId && (
                      <FormHelperText>{errors.serviceId}</FormHelperText>
                    )}
                  </FormControl>

                  {selectedService && (
                    <Paper 
                      variant="outlined" 
                      sx={{ 
                        p: 2, 
                        bgcolor: 'rgba(255, 255, 255, 0.9)',
                        borderColor: theme.palette.primary.main
                      }}
                    >
                      <Typography variant="subtitle1" color="primary" gutterBottom>
                        Service Details
                      </Typography>
                      <Typography variant="body2" paragraph>
                        {selectedService.description}
                      </Typography>
                      <Grid container spacing={2}>
                        <Grid item xs={6}>
                          <Typography variant="body2" color="text.secondary">
                            Duration: {selectedService.duration} minutes
                          </Typography>
                        </Grid>
                        <Grid item xs={6}>
                          <Typography variant="body2" color="text.secondary">
                            Price: ${selectedService.price}
                          </Typography>
                        </Grid>
                      </Grid>
                    </Paper>
                  )}
                </>
              )}

              {activeStep === 2 && (
                <>
                  <TextField
                    required
                    fullWidth
                    label="Full Name"
                    name="customerName"
                    value={formData.customerName}
                    onChange={handleChange}
                    InputProps={{
                      startAdornment: (
                        <PersonIcon sx={{ mr: 1, color: theme.palette.primary.main }} />
                      ),
                    }}
                  />
                  <TextField
                    required
                    fullWidth
                    type="email"
                    label="Email"
                    name="customerEmail"
                    value={formData.customerEmail}
                    onChange={handleChange}
                    error={!!errors.customerEmail}
                    helperText={errors.customerEmail}
                  />
                  <TextField
                    required
                    fullWidth
                    label="Phone Number"
                    name="customerPhone"
                    value={formData.customerPhone}
                    onChange={handleChange}
                    error={!!errors.customerPhone}
                    helperText={errors.customerPhone}
                  />
                </>
              )}

              {activeStep === 3 && (
                <>
                  <TextField
                    required
                    fullWidth
                    type="datetime-local"
                    label="Appointment Date & Time"
                    name="appointmentDate"
                    value={formData.appointmentDate}
                    onChange={handleChange}
                    InputLabelProps={{
                      shrink: true,
                    }}
                    InputProps={{
                      startAdornment: (
                        <CalendarIcon sx={{ mr: 1, color: theme.palette.primary.main }} />
                      ),
                    }}
                    error={!!errors.appointmentDate}
                    helperText={errors.appointmentDate}
                  />
                  <TextField
                    fullWidth
                    multiline
                    rows={4}
                    label="Additional Notes"
                    name="notes"
                    value={formData.notes}
                    onChange={handleChange}
                  />

                  <Paper 
                    variant="outlined" 
                    sx={{ 
                      p: 2, 
                      bgcolor: 'rgba(255, 255, 255, 0.9)',
                      borderColor: theme.palette.primary.main
                    }}
                  >
                    <Typography variant="h6" gutterBottom>
                      Booking Summary
                    </Typography>
                    <Grid container spacing={2}>
                      <Grid item xs={12} sm={6}>
                        <Typography variant="body2" color="text.secondary">
                          Bike: {formData.bikeBrand} {formData.bikeModel}
                        </Typography>
                      </Grid>
                      <Grid item xs={12} sm={6}>
                        <Typography variant="body2" color="text.secondary">
                          Year: {formData.manufactureYear}
                        </Typography>
                      </Grid>
                      <Grid item xs={12}>
                        <Typography variant="body2" color="text.secondary">
                          Service: {selectedService?.name}
                        </Typography>
                      </Grid>
                      <Grid item xs={12}>
                        <Typography variant="body2" color="text.secondary">
                          Price: ${selectedService?.price}
                        </Typography>
                      </Grid>
                    </Grid>
                  </Paper>
                </>
              )}

              <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 3 }}>
                <Button
                  onClick={handleBack}
                  disabled={activeStep === 0 || loading}
                  sx={{ mr: 1 }}
                >
                  Back
                </Button>
                <Box>
                  {activeStep === steps.length - 1 ? (
                    <Button
                      variant="contained"
                      type="submit"
                      disabled={!validateStep() || loading}
                      endIcon={loading ? <CircularProgress size={20} /> : <CheckIcon />}
                    >
                      {loading ? 'Submitting...' : 'Confirm Booking'}
                    </Button>
                  ) : (
                    <Button
                      variant="contained"
                      onClick={handleNext}
                      disabled={!validateStep() || loading}
                    >
                      Next
                    </Button>
                  )}
                </Box>
              </Box>
            </Stack>
          </Fade>
        </Box>
      </Paper>
    </Box>
  );
};

export default BookingForm;
